<script>
import anime from 'animejs';

export default {
  data() {
    return {
      translateX: 15,
      translateY: 15,
      inAnimationInstance: null,
      outAnimationInstance: null,
    };
  },
  computed: {
    computedTranslateX: {
      get() {
        return this.translateX;
      },
      set(v) {
        this.translateX = v;
      },
    },
    computedTranslateY: {
      get() {
        return this.translateY;
      },
      set(v) {
        this.translateY = v;
      },
    },
  },
  mounted() {
    this.$nextTick(() => {
      this.inAnimation();
    });
  },
  beforeDestroy() {
    this.inAnimationInstance = null;
    this.outAnimationInstance = null;
  },
  methods: {
    inAnimation() {
      this.inAnimationInstance = anime({
        targets: ".page__path",
        strokeDashoffset: [anime.setDashoffset, 0],
        easing: "easeInCirc",
        autoplay: true,
        duration: 1500,
        delay(el, i) {
          return i * 550;
        }
      });
    },
    mouseMoveAnimation(e) {
      const x = e.clientX;
      const y = e.clientY;
      const computedX = x / 100;
      const computedY = y / 100;
      this.computedTranslateX = computedX;
      this.computedTranslateY = computedY;
    }
  },
  beforeRouteLeave(to, from, next) {
    this.outAnimationInstance = anime({
      targets: '.page__path',
      strokeDashoffset: [0, anime.setDashoffset],
      easing: 'easeOutCirc',
      duration: 1500,
      delay(el, i) {
        return i * 550;
      },
      complete(anim) {
        if (anim.completed) {
          next();
        }
      },
    });
  },
